import React, { useState } from "react";
import './assetCategorization.css';

const Categorization = () => {
  const [category, setCategory] = useState(""); // Initialize state for 'category'
  const [customCategory, setCustomCategory] = useState(""); // Custom category
  const [types, setTypes] = useState(""); // Initialize state for 'types'
  const [customType, setCustomType] = useState(""); // Custom type

  const categories = ["Electronics", "Furniture", "Stationery", "Other"];
  const typesList = ["Laptop", "Chair", "Pen", "Other"];

  return (
    <div>
      <div className="asset-categorization">
        <div className="form-container">
          <h2>Asset Categorization</h2>
          <div className="input-box">
            <select
              value={category}
              onChange={(e) => setCategory(e.target.value)} // Make sure category state is being updated
            >
              <option value="">Select Category</option>
              {categories.map((cat) => (
                <option key={cat} value={cat}>
                  {cat}
                </option>
              ))}
            </select>
            {category === "Other" && (
              <input
                type="text"
                value={customCategory}
                onChange={(e) => setCustomCategory(e.target.value)} // Make sure customCategory state is updated
                placeholder="Enter Category"
              />
            )}

            <select
              value={types}
              onChange={(e) => setTypes(e.target.value)} // Make sure types state is updated
            >
              <option value="">Select Type</option>
              {typesList.map((typ) => (
                <option key={typ} value={typ}>
                  {typ}
                </option>
              ))}
            </select>
            {types === "Other" && (
              <input
                type="text"
                value={customType}
                onChange={(e) => setCustomType(e.target.value)} // Make sure customType state is updated
                placeholder="Enter Type"
              />
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Categorization;
