import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./users.css";
import axios from "axios";

const Users = () => {
  const [users, setUsers] = useState([]);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editingUser, setEditingUser] = useState(null);

  const fetchUsers = async () => {
    try {
      const response = await axios.get("http://localhost:8000/api/users/getallUsers");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleBlock = async (id) => {
    if (window.confirm("Are you sure you want to block this user?")) {
      try {
        const response = await axios.patch(`http://localhost:8000/api/users/block/${id}`);
        alert(response.data.message || "User blocked successfully!");
        fetchUsers();
      } catch (error) {
        console.error("Error blocking user:", error);
        alert(error.response?.data?.message || "Failed to block the user.");
      }
    }
  };

  const handleUnblock = async (id) => {
    if (window.confirm("Are you sure you want to unblock this user?")) {
      try {
        const response = await axios.patch(`http://localhost:8000/api/users/unblock/${id}`);
        alert(response.data.message || "User unblocked successfully!");
        fetchUsers();
      } catch (error) {
        console.error("Error unblocking user:", error);
        alert(error.response?.data?.message || "Failed to unblock the user.");
      }
    }
  };

  const handleEdit = (user) => {
    setEditingUser(user);
    setShowEditModal(true);
  };

  const handleUpdate = async (updatedUser) => {
    try {
      const response = await axios.put(
        `http://localhost:8000/api/users/${updatedUser._id}`,
        updatedUser
      );
      alert("User updated successfully!");
      fetchUsers();
      setShowEditModal(false);
    } catch (error) {
      console.error("Error updating user:", error);
      alert("An error occurred while updating the user.");
    }
  };

  return (
    <div className="row">
      <div className="col-md-12">
        <table className="table table-bordered table-light">
          <thead className="bs">
            <tr>
              <th>ID</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Designation</th>
              <th>Contact</th>
              <th>User Name</th>
              <th>User Role</th>
              <th>Company Name</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.length > 0 ? (
              users.map((user) => (
                <tr key={user._id}>
                  <td>{user._id}</td>
                  <td>{user.firstName}</td>
                  <td>{user.lastName}</td>
                  <td>{user.designation}</td>
                  <td>{user.contact}</td>
                  <td>{user.username}</td>
                  <td>{user.selectedOption}</td>
                  <td>{user.companyName}</td>
                  <td>
                    <button
                      className="btn btn-primary btn-sm me-2"
                      onClick={() => handleEdit(user)}
                    >
                      Update
                    </button>
                    {user.isBlocked ? (
                      <button
                        className="btn btn-success btn-sm"
                        onClick={() => handleUnblock(user._id)}
                      >
                        Unblock
                      </button>
                    ) : (
                      <button
                        className="btn btn-warning btn-sm"
                        onClick={() => handleBlock(user._id)}
                      >
                        Block
                      </button>
                    )}
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="9" className="text-center">
                  No user data available
                </td>
              </tr>
            )}
          </tbody>
        </table>

        {showEditModal && editingUser && (
          <EditUserModal
            show={showEditModal}
            onClose={() => setShowEditModal(false)}
            user={editingUser}
            onUpdate={handleUpdate}
          />
        )}
      </div>
    </div>
  );
};

const EditUserModal = ({ show, onClose, user, onUpdate }) => {
  const [updatedUser, setUpdatedUser] = useState(user);
  const [errors, setErrors] = useState({});

  useEffect(() => {
    setUpdatedUser(user);
  }, [user]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedUser((prev) => ({ ...prev, [name]: value }));
  };

  const validate = () => {
    const newErrors = {};
    if (!updatedUser.firstName) newErrors.firstName = "First name is required.";
    if (!updatedUser.lastName) newErrors.lastName = "Last name is required.";
    if (!updatedUser.designation) newErrors.designation = "Designation is required.";
    if (!updatedUser.contact) newErrors.contact = "Contact is required.";
    if (!updatedUser.username) newErrors.username = "Username is required.";
    if (!updatedUser.companyName) newErrors.companyName = "Company name is required.";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = () => {
    if (validate()) {
      onUpdate(updatedUser);
    }
  };

  return (
    <div className={`modal fade ${show ? "show d-block" : "d-none"}`} tabIndex="-1">
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">Edit User</h5>
            <button type="button" className="btn-close" onClick={onClose}></button>
          </div>
          <div className="modal-body">
            {["firstName", "lastName", "designation", "contact", "username", "companyName"].map(
              (field) => (
                <div className="mb-3" key={field}>
                  <label className="form-label">{field.replace(/^\w/, (c) => c.toUpperCase())}</label>
                  <input
                    type="text"
                    className="form-control"
                    name={field}
                    value={updatedUser[field] || ""}
                    onChange={handleChange}
                  />
                  {errors[field] && <small className="text-danger">{errors[field]}</small>}
                </div>
              )
            )}
            <div className="mb-3">
              <label className="form-label">User Role</label>
              <select
                className="form-select"
                name="selectedOption"
                value={updatedUser.selectedOption || ""}
                onChange={handleChange}
              >
                <option value="">Select Role</option>
                <option value="Admin">Admin</option>
                <option value="CompanyAdmin">Company Admin</option>
              </select>
            </div>
          </div>
          <div className="modal-footer">
            <button type="button" className="btn btn-secondary" onClick={onClose}>
              Close
            </button>
            <button type="button" className="btn btn-primary" onClick={handleSubmit}>
              Save Changes
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Users;
