import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Alert } from "react-bootstrap";
import './login.css'

function LoginForm() {
  const [state, setState] = useState({ email: "", password: "" });
  const [errors, setErrors] = useState({ email: "", password: "" });
  const [showError, setShowError] = useState(false);
  const navigate = useNavigate();

  const handleChange = (evt) => {
    const { name, value } = evt.target;

    setState((prev) => ({ ...prev, [name]: value }));
    setErrors((prev) => ({ ...prev, [name]: "" })); // Clear error
  };

  const validate = () => {
    let valid = true;
    const newErrors = { email: "", password: "" };

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(state.email)) {
      newErrors.email = "Please enter a valid email address.";
      valid = false;
    }

    if (state.password.length < 6) {
      newErrors.password = "Password must be at least 6 characters long.";
      valid = false;
    }

    setErrors(newErrors);
    return valid;
  };

  const handleOnSubmit = async (evt) => {
    evt.preventDefault();

    if (validate()) {
      try {
        const result = await axios.post("http://localhost:5000/api/users/SignInForm", state);
        console.log(result.data);

        setState({ email: "", password: "" });
        setShowError(false); // Hide error

        localStorage.setItem("currentUser", JSON.stringify(result.data));
        navigate("/"); // Navigate to the home page
      } catch (error) {
        console.error(error);
        setShowError(true);
      }
    } else {
      setShowError(true);
    }
  };

  return (

    <div className="logapp">
    <div className={containerClass} id="container">
      <div className="container1">
        <SignUpForm />
        <SignInForm /><div className="logapp">
          <div className={containerClass} id="container">
            <div className="container1">
              <SignUpForm />
              <SignInForm />
              <div className="overlay-container">
                <div className="overlay">
                  <div className="overlay-panel overlay-left">
                    <h1 className="title">Welcome Back!</h1>
                    <p className="p">
                      If you already have an account, please log in here
                    </p>
                    <button
                      className="ghost button"
                      id="signIn"
                      onClick={() => handleOnClick("signIn")}
                    >
                      Sign In
                    </button>
                  </div>
                  
                </div>
              </div>
            </div>
          </div>
        <div className="overlay-container">
          <div className="overlay">
            <div className="overlay-panel overlay-left">
              <h1 className="title">Welcome Back!</h1>
              <p className="p">
                If you already have an account, please log in here
              </p>
              <button
                className="ghost button"
                id="signIn"
                onClick={() => handleOnClick("signIn")}
              >
                Sign In
              </button>
            </div>
            <div className="overlay-panel overlay-right">
              <h1 className="title">Hello, Customer!</h1>
              <p className="p">Don't have an account? Sign up here</p>
              <button
                className="ghost button"
                id="signUp"
                onClick={() => handleOnClick("signUp")}
              >
                Sign Up
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
</div>
</div>






    
  );
}

export default LoginForm;
