import React, { useState } from 'react';
import './login.css';

const Login = () => {
  const [username, setUsername] = useState(''); // State for username
  const [password, setPassword] = useState(''); // State for password
  const [error, setError] = useState(''); // State for error messages

  // Function to validate form inputs and handle submission
  const validateForm = (e) => {
    e.preventDefault();

    if (!username || !password) {
      setError('Username and password are required.');
      return;
    }

    // Example: Mock login API call (replace with actual API logic)
    fetch('http://localhost:8000//api/AdminUsersRole/Login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password }),
    })
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error('Invalid credentials');
      })
      .then(() => {
        setError(''); // Clear error
        alert('Login successful!');
      })
      .catch((err) => {
        setError(err.message || 'Something went wrong.');
      });
  };

  return (
    <div className="main">
      <div className="container logmain">
        <div className="panel sign-in-panel">
          <h1 className="titleleft">Welcome to Asset Management System</h1>
        </div>
        <div className="panel sign-up-panel">
          <h1 className="titleright">Log In to Your Account</h1>
          <form className="signup-form" onSubmit={validateForm}>
            {error && <p className="error-message">{error}</p>}
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button type="submit" className="sign-up-button">
              LOGIN
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
