import './adduserrole.css';
import { React, useState } from "react";

const AddUsersRole = () => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [Designation, setDesignation] = useState("");
    const [contact, setContact] = useState("");
    const [subjects, setSubjects] = useState({
        Admin: true,
        CompanyAdmin: false,
    });
    const [Username, setUsername] = useState("");
    const [selectedOption, setSelectedOption] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [showPassword, setShowPassword] = useState(false);
    const [Companyname, setCompanyname] = useState("");
    const [errors, setErrors] = useState({});
    const [passwordStrength, setPasswordStrength] = useState("");

    const validateForm = () => {
        let formErrors = {};
        if (!firstName) formErrors.firstName = "First Name is required";
        if (!lastName) formErrors.lastName = "Last Name is required";
        if (!Designation) formErrors.Designation = "Designation is required";
        if (!contact) formErrors.contact = "Contact is required";
        if (!Username) formErrors.Username = "Username is required";
        if (!password) formErrors.password = "Password is required";
        if (!confirmPassword) formErrors.confirmPassword = "Confirm Password is required";
        if (!Companyname) formErrors.Branchname = "Branch Name is required";
        if (password && !validatePasswordStrength(password)) {
            formErrors.passwordStrength = "Password must be at least 8 characters long, include an uppercase letter, a lowercase letter, a number, and a special character.";
        }
        setErrors(formErrors);
        return Object.keys(formErrors).length === 0;
    };

    const validatePasswordStrength = (password) => {
        const strongPasswordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[A-Za-z\d@$!%*?&]{8,}$/;
        return strongPasswordRegex.test(password);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!validateForm()) return;

        if (password !== confirmPassword) {
            alert("Passwords do not match!");
            return;
        }

        console.log(
            firstName,
            lastName,
            Designation,
            contact,
            subjects,
            Username,
            selectedOption,
            password,
            Branchname
        );
        // Add your form submission logic here
    };

    const handleReset = () => {
        setFirstName("");
        setLastName("");
        setDesignation("");
        setContact("");
        setSubjects({
            Admin: true,
            BranchAdmin: false,
        });
        setUsername("");
        setSelectedOption("");
        setPassword("");
        setConfirmPassword("");
        setShowPassword(false);
        setBranchname("");
        setErrors({});
        setPasswordStrength("");
    };

    return (
        <div className="Users">
            <h1>User Registration</h1>
            <fieldset>
                <form action="#" method="get">
                    <label htmlFor="firstname">First Name*</label>
                    <input
                        type="text"
                        name="firstname"
                        id="firstname"
                        value={firstName}
                        onChange={(e) => setFirstName(e.target.value)}
                        placeholder="Enter First Name"
                        required
                    />
                    {errors.firstName && <span className="error">{errors.firstName}</span>}

                    <label htmlFor="lastname">Last Name*</label>
                    <input
                        type="text"
                        name="lastname"
                        id="lastname"
                        value={lastName}
                        onChange={(e) => setLastName(e.target.value)}
                        placeholder="Enter Last Name"
                        required
                    />
                    {errors.lastName && <span className="error">{errors.lastName}</span>}

                    <label htmlFor="Designation">Designation*</label>
                    <input
                        type="text"
                        name="Designation"
                        id="Designation"
                        value={Designation}
                        onChange={(e) => setDesignation(e.target.value)}
                        placeholder="Enter Designation"
                        required
                    />
                    {errors.Designation && <span className="error">{errors.Designation}</span>}

                    <label htmlFor="tel">Contact*</label>
                    <input
                        type="tel"
                        name="contact"
                        id="contact"
                        value={contact}
                        onChange={(e) => setContact(e.target.value)}
                        placeholder="Enter Mobile number"
                        required
                    />
                    {errors.contact && <span className="error">{errors.contact}</span>}

                    <label htmlFor="Username">User Name*</label>
                    <input
                        type="text"
                        name="Username"
                        id="Username"
                        value={Username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder="Enter User Name"
                        required
                    />
                    {errors.Username && <span className="error">{errors.Username}</span>}

                    <label htmlFor="password">Password*</label>
                    <input
                        type={showPassword ? "text" : "password"}
                        name="password"
                        id="password"
                        value={password}
                        onChange={(e) => {
                            setPassword(e.target.value);
                            setPasswordStrength(validatePasswordStrength(e.target.value) ? "Strong" : "Weak");
                        }}
                        placeholder="Enter Password"
                        required
                    />
                    {errors.password && <span className="error">{errors.password}</span>}
                    <span className={passwordStrength === "Strong" ? "strong" : "weak"}>
                        {passwordStrength}
                    </span>

                    <label htmlFor="confirmPassword">Confirm Password*</label>
                    <input
                        type={showPassword ? "text" : "password"}
                        name="confirmPassword"
                        id="confirmPassword"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        placeholder="Confirm Password"
                        required
                    />
                    {errors.confirmPassword && <span className="error">{errors.confirmPassword}</span>}
                    <label>
                        <input
                            type="checkbox"
                            checked={showPassword}
                            onChange={() => setShowPassword(!showPassword)}
                        />
                        Show Password
                    </label>

                    <label>User Role</label>
                    <select
                        name="select"
                        id="select"
                        value={selectedOption}
                        onChange={(e) => setSelectedOption(e.target.value)}
                    >
                        <optgroup label="Role">
                            <option value="1">Admin</option>
                            <option value="2">Branch Admin</option>
                        </optgroup>
                    </select>

                    <label htmlFor="Branchname">Branch Name*</label>
                    <input
                        type="text"
                        name="Branchname"
                        id="Branchname"
                        value={Companyname}
                        onChange={(e) => setBranchname(e.target.value)}
                        placeholder="Enter Branch Name"
                        required
                    />
                    {errors.Branchname && <span className="error">{errors.Branchname}</span>}

                    <button type="reset" value="reset" onClick={handleReset}>
                        Reset
                    </button>
                    <button type="submit" value="Submit" onClick={handleSubmit}>
                        Submit
                    </button>
                </form>
            </fieldset>
        </div>
    );
};

export default AddUsersRole;
