import React, { useState, useEffect } from "react";
//import { Tabs } from "antd";
import "bootstrap/dist/css/bootstrap.min.css";
import "./users.css";
import axios from "axios";


const Users = () => {
  const [technicians, setTechnicians] = useState([]);
  const [editingTechnician, setEditingTechnician] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);

  // Fetch technicians on component mount
  useEffect(() => {
    fetchTechnicians();
  }, []);

  const fetchTechnicians = async () => {
    try {
      const response = await axios.get("/api/technicians/gettechnician");
      setTechnicians(response.data);
    } catch (error) {
      console.error("Error fetching Technician:", error);
    }
  };

  const BlockUser = async (id) => {
    if (window.confirm("Are you sure you want to delete this technician?")) {
      try {
        const response = await axios.delete(`/api/technicians/${id}`);
        if (response.status === 200) {
          alert("Technician deleted successfully!");
          fetchTechnicians(); // Refresh the list after deletion
        } else {
          alert("Failed to delete the technician. Please try again.");
        }
      } catch (error) {
        console.error("Error deleting technician:", error);
        alert("An error occurred while deleting the technician.");
      }
    }
  };

  const handleEdit = (technician) => {
    setEditingTechnician(technician);
    setShowEditModal(true);
  };

  const handleUpdate = async (id, updatedData) => {
    try {
      await axios.put(`/api/technicians/${id}`, updatedData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("Technician updated successfully!");
      setShowEditModal(false); // Close modal after update
      fetchTechnicians(); // Refresh data
    } catch (error) {
      console.error("Error updating technician:", error);
      alert("Failed to update technician.");
    }
  };

  return (
    <div className="row">
      <div className="col-md-12">
        <table className="table table-bordered table-light">
          <thead className="bs">
            <tr>
              
              <th>ID</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Designation</th>
              <th>Contact</th>
              <th>User Role</th>
              <th>Company Name</th>

            </tr>
          </thead>
          <tbody>
            {users.length > 0 ? (
              users.map((user) => (
                <tr key={user._id}>
                  <td>{user._id}</td>
                  <td>{user.technicianName}</td>
                  <td>{user.serviceType}</td>
                 
                  <td>
                    <button
                      className="btn btn-primary btn-sm me-2"
                      onClick={() => handleEdit(technician)}
                    >
                      Update
                    </button>
                    <button
                      className="btn btn-danger btn-sm"
                      onClick={() => BlockUser(technician._id)}
                    >
                       Block User
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6" className="text-center">
                  No Technician Data Available
                </td>
              </tr>
            )}
          </tbody>
        </table>

        {/* Edit Modal */}
        {showEditModal && editingTechnician && (
          <users
            show={showEditModal}
            onClose={() => setShowEditModal(false)}
            technician={editingTechnician}
            onUpdate={handleUpdate}
          />
        )}
      </div>
    </div>
  );
}
export default Users
