import React, { useState, useRef, useEffect } from "react";
import { QRCode } from "react-qrcode-logo";
import html2canvas from "html2canvas";
import "./assetRegister.css";
import axios from "axios";

const AssetRegister = () => {
  const user = JSON.parse(localStorage.getItem("currentUser"));

  const [name, setName] = useState(user?.username || "");
  const [company, setCompany] = useState("");
  const [department, setDepartment] = useState("");
  const [mainCategory, setMainCategory] = useState("");
  const [type, setType] = useState("");
  const [categories, setCategories] = useState([]);
  const [types, setTypes] = useState([]);
  const [assetName, setAssetName] = useState("");
  const [assetUpdateDate, setAssetUpdateDate] = useState("");
  const [serialNumber, setSerialNumber] = useState("");
  const [qrCodeData, setQrCodeData] = useState(null);
  const [trackingId, setTrackingId] = useState("");

  const qrCodeContainerRef = useRef();

  const mainCategories = [
    "Electronic items",
    "Laundry & Linen",
    "Housekeeping Supplies",
    "Electrical items",
    "Security & Safety",
    "Furniture",
    "Outdoor & Garden Equipment",
    "Stationery",
  ];

  const companies = ["Vella", "98 Acers", "Ravana Pool Club", "Flying Ravana", "Le Maas Tota", "Tea Factory"];
  const departments = ["IT", "HR", "Kitchen", "Front Office","Store","Account","Audit"];

  useEffect(() => {
    fetchCategories();
  }, []);

  useEffect(() => {
    if (mainCategory) {
      filterTypes();
    } else {
      setTypes([]);
    }
  }, [mainCategory, categories]);

  const fetchCategories = async () => {
    try {
      const response = await axios.get("http://localhost:8000/api/categories/getCategory");
      setCategories(response.data);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  const filterTypes = () => {
    if (mainCategory) {
      const filtered = categories
        .filter((category) => category.category === mainCategory)
        .map((category) => category.types)
        .flat();
      setTypes(filtered);
    }
  };

  const generateTrackingId = () => {
    const companyShortCodes = {
      Vella: "VE",
      "98 Acers": "98",
      "Ravana Pool Club": "RPC",
      "Flying Ravana": "FR",
      "Le Maas Tota": "LMT",
      "Tea Factory": "TF",
    };
  
    const departmentShortCodes = {
      IT: "IT",
      HR: "HR",
      Kitchen: "KT",
      Store: "ST",
      "Front Office": "FO",
      Account: "AC",
      Audit : "AU"
    };
  
    const companyCode = companyShortCodes[company] || "XX";
    const departmentCode = departmentShortCodes[department] || "XX";
  
    const randomNum = String(Math.floor(Math.random() * 100000)).padStart(5, "0");

    if (serialNumber) {
      const serialSuffix = serialNumber.slice(-4).padStart(4, "0");
      return `${companyCode}-${departmentCode}-${serialSuffix}`;
    }
  
    return `${companyCode}-${departmentCode}-${randomNum}`;
  };

  const handleGenerateQR = () => {
    if (!name || !company || !department || !mainCategory || !assetName || !assetUpdateDate || !type) {
      alert("Please fill in all fields before generating the QR code.");
      return;
    }

    if (mainCategory === "Electronic items" && !serialNumber) {
      alert("Please enter a Serial Number for Electronics.");
      return;
    }

    const uniqueTrackingId = generateTrackingId();
    setTrackingId(uniqueTrackingId);
  };

  useEffect(() => {
    if (trackingId) {
      const qrData = JSON.stringify({
        name,
        company,
        department,
        mainCategory,
        assetName,
        type,
        assetUpdateDate,
        serialNumber: mainCategory === "Electronic items" ? serialNumber : null,
        trackingId,
      });

      setQrCodeData(qrData);
    }
  }, [trackingId]); // Updates QR code data when tracking ID changes

  const handleDownloadQR = async () => {
    if (!qrCodeContainerRef.current) return;

    try {
      const canvas = await html2canvas(qrCodeContainerRef.current);
      const image = canvas.toDataURL("image/png");
      const link = document.createElement("a");
      link.href = image;
      link.download = `${trackingId}_QR.png`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (error) {
      console.error("Error downloading QR code:", error);
    }
  };

  return (
    <div className="asset-register">
      <div className="form-container">
        <h2>Asset Registration</h2>
        <div className="input-box">
          <input type="text" value={name} placeholder="Enter your Name" readOnly={Boolean(user)} />

          <select value={company} onChange={(e) => setCompany(e.target.value)}>
            <option value="">Select Company</option>
            {companies.map((com) => (
              <option key={com} value={com}>{com}</option>
            ))}
          </select>

          <select value={department} onChange={(e) => setDepartment(e.target.value)}>
            <option value="">Select Department</option>
            {departments.map((dep) => (
              <option key={dep} value={dep}>{dep}</option>
            ))}
          </select>

          <select value={mainCategory} onChange={(e) => setMainCategory(e.target.value)}>
            <option value="">Select Category</option>
            {mainCategories.map((cat) => (
              <option key={cat} value={cat}>{cat}</option>
            ))}
          </select>

          {mainCategory && (
            <select value={type} onChange={(e) => setType(e.target.value)}>
              <option value="">Select Type</option>
              {types.map((t, index) => (
                <option key={index} value={t}>{t}</option>
              ))}
            </select>
          )}

          <input type="text" value={assetName} onChange={(e) => setAssetName(e.target.value)} placeholder="Enter Asset Name" />

          {mainCategory === "Electronic items" && (
            <input type="text" value={serialNumber} onChange={(e) => setSerialNumber(e.target.value)} placeholder="Enter Serial Number" />
          )}

          <input type="date" value={assetUpdateDate} onChange={(e) => setAssetUpdateDate(e.target.value)} />

          {trackingId && (
            <div className="qr-container" ref={qrCodeContainerRef}>
              <p>Tracking ID: {trackingId}</p>
              {qrCodeData && <QRCode value={qrCodeData} size={150} />}
              <button className="button download-btn" onClick={handleDownloadQR}>Download QR Code</button>
            </div>
          )}

          <div className="button-group">
            <button className="button generate-btn" onClick={handleGenerateQR}>Generate QR</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AssetRegister;
