import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './login.css';

const Login = () => {
  //const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const validateForm = async (e) => {
    e.preventDefault();

    if (!username || !password) {
      setError('Both username and password are required.');
      return;
    }

    // Optional: Add email validation logic if email login is required
    if (email) {
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(email)) {
        setError('Please enter a valid email address.');
        return;
      }
    }

    try {
      const response = await axios.post('http://localhost:8000/api/users/Login', {
        UserName: username,
        password: password,
      });

      setError('');
      alert('Login successful');
      localStorage.setItem('currentUser', JSON.stringify(response.data));
      navigate('/AdminDashboardPage');
    } catch (err) {
      console.error(err);
      setError('Invalid username or password. Please try again.');
    }
  };

  return (
    <div className="main">
      <div className="container logmain">
        <div className="panel sign-in-panel">
          <h1 className="titleleft">Welcome to Asset Management System</h1>
        </div>
        <div className="panel sign-up-panel">
          <h1 className="titleright">Log In to Your Account</h1>
          <form className="signup-form" onSubmit={validateForm}>
            {error && <p className="error-message">{error}</p>}
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button type="submit" className="sign-up-button">
              LOGIN
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
