import React, { useState } from 'react';
import './login.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const [state, setState] = useState({
    email: '',
    password: ''
  });
  const [errors, setErrors] = useState({
    email: '',
    password: ''
  });
  const [showError, setShowError] = useState(false); // Error alert state
  const navigate = useNavigate(); // Hook for navigation

  const handleChange = (evt) => {
    const { name, value } = evt.target;

    setState({
      ...state,
      [name]: value
    });

    // Clear error when user modifies input
    setErrors({
      ...errors,
      [name]: ''
    });
  };

  const validate = () => {
    let valid = true;
    const newErrors = { email: '', password: '' };

    // Email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(state.email)) {
      newErrors.email = 'Please enter a valid email address.';
      valid = false;
    }

    // Password validation (minimum 6 characters)
    if (state.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters long.';
      valid = false;
    }

    setErrors(newErrors);
    return valid;
  };

  const handleOnSubmit = async (evt) => {
    evt.preventDefault();

    if (validate()) {
      try {
        const result = await axios.post('http://localhost:5000/api/users/login', state);
        console.log(result.data);

        // Reset email and password fields
        setState({ email: '', password: '' });
        setShowError(false); // Hide error on successful login
        localStorage.setItem('currentUser', JSON.stringify(result.data));
        navigate('/');
      } catch (error) {
        setShowError(true);
        console.error('Login failed:', error);
      }
    }
  };

  return (
    <div className="main">
      <div className="container logmain">
        <div className="panel sign-in-panel">
          <h1 className="titleleft">Welcome to Asset Management System</h1>
        </div>
        <div className="panel sign-up-panel">
          <h1 className="titleright">Log In to Your Account</h1>
          <form className="signup-form" onSubmit={handleOnSubmit}>
            {showError && <p className="error-message">Invalid login credentials</p>}
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={state.email}
              onChange={handleChange}
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={state.password}
              onChange={handleChange}
            />
            <button type="submit" className="sign-up-button">
              LOGIN
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
