import React, { useState } from 'react';
import axios from 'axios'; // Import axios for API calls
import { useNavigate } from 'react-router-dom'; // Use for navigation
import './login.css';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [selectedOption, setSelectedOption] = useState(''); // State to track the selected option
  const [error, setError] = useState('');
  const navigate = useNavigate(); // React Router navigation hook

  const validateForm = async (e) => {
    e.preventDefault();

    if (!username || !password || !selectedOption) {
      setError('All fields are required, including the role.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:8000/api/users/Login', {
        UserName: username,
        password: password,
        selectedOption

      });

      // Store user data in localStorage
      localStorage.setItem('currentUser', JSON.stringify(response.data));
      setError(''); // Clear any previous errors

      // Navigate based on the selected option
      if (selectedOption === 'Admin') {
        navigate('/AdminDashboardPage');
      } else if (selectedOption === 'CompanyAdmin') {
        navigate('/CompanyDashBord');
      } else {
        setError('Invalid role selected.');
      }
    } catch (err) {
      console.error(err);
      setError('Invalid username or password. Please try again.');
    }
  };

  return (
    <div className="main">
      <div className="container logmain">
        <div className="panel sign-in-panel">
          <h1 className="titleleft">Welcome to Asset Management System</h1>
        </div>
        <div className="panel sign-up-panel">
          <h1 className="titleright">Log In to Your Account</h1>
          <form className="signup-form" onSubmit={validateForm}>
            {error && <p className="error-message">{error}</p>}
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <select
              value={selectedOption}
              onChange={(e) => setSelectedOption(e.target.value)}
              className="role-select"
            >
              <option value="">Select Role</option>
              <option value="admin">Admin</option>
              <option value="companyAdmin">Company Admin</option>
            </select>
            <button type="submit" className="sign-up-button">
              LOGIN
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
