import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Alert } from "react-bootstrap";
import './login.css'

function LoginForm() {
  const [state, setState] = useState({ email: "", password: "" });
  const [errors, setErrors] = useState({ email: "", password: "" });
  const [showError, setShowError] = useState(false);
  const navigate = useNavigate();

  const handleChange = (evt) => {
    const { name, value } = evt.target;

    setState((prev) => ({ ...prev, [name]: value }));
    setErrors((prev) => ({ ...prev, [name]: "" })); // Clear error
  };

  const validate = () => {
    let valid = true;
    const newErrors = { email: "", password: "" };

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(state.email)) {
      newErrors.email = "Please enter a valid email address.";
      valid = false;
    }

    if (state.password.length < 6) {
      newErrors.password = "Password must be at least 6 characters long.";
      valid = false;
    }

    setErrors(newErrors);
    return valid;
  };

  const handleOnSubmit = async (evt) => {
    evt.preventDefault();

    if (validate()) {
      try {
        const result = await axios.post("http://localhost:5000/api/users/SignInForm", state);
        console.log(result.data);

        setState({ email: "", password: "" });
        setShowError(false); // Hide error

        localStorage.setItem("currentUser", JSON.stringify(result.data));
        navigate("/"); // Navigate to the home page
      } catch (error) {
        console.error(error);
        setShowError(true);
      }
    } else {
      setShowError(true);
    }
  };

  return (
    <div className="form-container sign-in-container logapp  ">
      <form className="form" onSubmit={handleOnSubmit}>
        {showError && (
          <Alert variant="danger" className="mt-2">
            Invalid email or password. Please try again.
          </Alert>
        )}
        <h1 className="title pb-3">Sign in</h1>
        <input
          className={`input ${errors.email && "is-invalid"}`}
          type="email"
          placeholder="Email"
          name="email"
          value={state.email}
          onChange={handleChange}
        />
        {errors.email && <small className="text-danger">{errors.email}</small>}

        <input
          className={`input ${errors.password && "is-invalid"}`}
          type="password"
          name="password"
          placeholder="Password"
          value={state.password}
          onChange={handleChange}
        />
        {errors.password && <small className="text-danger">{errors.password}</small>}

        <button className="button mt-3" type="submit">
          Log In
        </button>
      </form>
    </div>
  );
}

export default LoginForm;
