import React, { useState, useRef, useEffect } from "react";
import { QRCode } from "react-qrcode-logo";
import html2canvas from "html2canvas";
import axios from "axios";
import "./assetRegister.css";

const AssetRegister = () => {
  const user = JSON.parse(localStorage.getItem("currentUser"));

  const [name, setName] = useState(user?.username || "");
  const [company, setCompany] = useState("");
  const [department, setDepartment] = useState("");
  const [mainCategory, setMainCategory] = useState("");
  const [type, setType] = useState("");
  const [categories, setCategories] = useState([]);
  const [types, setTypes] = useState([]);
  const [assetName, setAssetName] = useState("");
  const [assetUpdateDate, setAssetUpdateDate] = useState("");
  const [serialNumber, setSerialNumber] = useState("");
  const [qrCodeData, setQrCodeData] = useState(null);
  const [trackingId, setTrackingId] = useState("");
  const [specialNote, setSpecialNote] = useState("");
  const [customType, setCustomType] = useState("");
  const [computerComponents, setComputerComponents] = useState({
    fullPack: false,
    monitor: false,
    cpu: false,
    mouse: false,
    keyboard: false,
  });

  const qrCodeContainerRef = useRef();

  useEffect(() => {
    fetchCategories();
  }, []);

  useEffect(() => {
    if (mainCategory) {
      filterTypes();
    } else {
      setTypes([]);
    }
  }, [mainCategory, categories]);

  const fetchCategories = async () => {
    try {
      const response = await axios.get("http://localhost:8000/api/categories/getCategory");
      setCategories(response.data);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  const filterTypes = () => {
    if (mainCategory) {
      const filtered = categories
        .filter((category) => category.category === mainCategory)
        .map((category) => category.types)
        .flat();
      setTypes(filtered);
    }
  };

  const handleGenerateQR = () => {
    if (!name || !company || !department || !mainCategory || !assetName || !assetUpdateDate || !type) {
      alert("Please fill in all fields before generating the QR code.");
      return;
    }

    if (mainCategory === "Electronic items" && !serialNumber) {
      alert("Please enter a Serial Number for Electronics.");
      return;
    }

    const selectedComponents = Object.keys(computerComponents).filter(key => computerComponents[key]);
    
    const qrData = JSON.stringify({
      name,
      company,
      department,
      mainCategory,
      assetName,
      type: type === "Other" ? customType : type,
      assetUpdateDate,
      serialNumber: mainCategory === "Electronic items" ? serialNumber : null,
      trackingId,
      components: selectedComponents.length > 0 ? selectedComponents : undefined,
    });

    setQrCodeData(qrData);
  };

  const handleComponentChange = (e) => {
    const { name, checked } = e.target;
    setComputerComponents((prev) => ({
      ...prev,
      [name]: checked,
    }));
  };

  return (
    <div className="asset-register">
      <div className="form-container">
        <h2>Asset Registration</h2>
        <div className="input-box">
          <select value={type} onChange={(e) => setType(e.target.value)}>
            <option value="">Select Type</option>
            {types.map((t, index) => (
              <option key={index} value={t}>{t}</option>
            ))}
            <option value="Other">Other</option>
          </select>

          {type === "Other" && (
            <input
              type="text"
              value={customType}
              onChange={(e) => setCustomType(e.target.value)}
              placeholder="Enter Custom Type"
            />
          )}

          {type === "Computer" && (
            <div className="computer-options">
              <label><input type="checkbox" name="fullPack" checked={computerComponents.fullPack} onChange={handleComponentChange} /> Full Pack</label>
              <label><input type="checkbox" name="monitor" checked={computerComponents.monitor} onChange={handleComponentChange} /> Monitor</label>
              <label><input type="checkbox" name="cpu" checked={computerComponents.cpu} onChange={handleComponentChange} /> CPU</label>
              <label><input type="checkbox" name="mouse" checked={computerComponents.mouse} onChange={handleComponentChange} /> Mouse</label>
              <label><input type="checkbox" name="keyboard" checked={computerComponents.keyboard} onChange={handleComponentChange} /> Keyboard</label>
            </div>
          )}

          <button className="button generate-btn" onClick={handleGenerateQR}>Generate QR</button>

          {qrCodeData && (
            <div className="qr-container">
              <h3>Generated QR Code</h3>
              <div ref={qrCodeContainerRef}>
                <QRCode value={qrCodeData} size={200} />
                <p>ID: {trackingId}</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AssetRegister;
