import React, { useState, useRef, useEffect } from "react";
import { QRCode } from "react-qrcode-logo";
import html2canvas from "html2canvas";
import "./assetRegister.css";
import axios from "axios";

const AssetRegister = () => {
  const user = JSON.parse(localStorage.getItem("currentUser"));
  const [name, setName] = useState(user?.username || "");
  const [company, setCompany] = useState("");
  const [department, setDepartment] = useState("");
  const [mainCategory, setMainCategory] = useState("");
  const [type, setType] = useState("");
  const [categories, setCategories] = useState([]);
  const [types, setTypes] = useState([]);
  const [assetName, setAssetName] = useState("");
  const [assetUpdateDate, setAssetUpdateDate] = useState("");
  const [serialNumber, setSerialNumber] = useState("");
  const [qrCodeData, setQrCodeData] = useState(null);
  const [trackingId, setTrackingId] = useState("");

  const qrCodeContainerRef = useRef();

  useEffect(() => {
    fetchCategories();
  }, []);

  useEffect(() => {
    if (mainCategory) {
      filterTypes();
    } else {
      setTypes([]);
    }
  }, [mainCategory, categories]);

  const fetchCategories = async () => {
    try {
      const response = await axios.get("http://localhost:8000/api/categories/getCategory");
      setCategories(response.data);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  const generateTrackingId = () => {
    const companyShortCodes = {
      Vella: "VE",
      "98 Acers": "98",
      "Ravana Pool Club": "RPC",
      "Flying Ravana": "FR",
      "Le Maas Tota": "LMT",
      "Tea Factory": "TF",
    };

    const departmentShortCodes = {
      IT: "IT",
      HR: "HR",
      Kitchen: "KT",
      Store: "ST",
      "Front Office": "FO",
      Account: "AC",
      Audit: "AU",
    };

    const companyCode = companyShortCodes[company] || "XX";
    const departmentCode = departmentShortCodes[department] || "XX";
    const randomNum = String(Math.floor(Math.random() * 100000)).padStart(5, "0");
    
    return serialNumber ? `${companyCode}-${departmentCode}-${serialNumber.slice(-4)}` : `${companyCode}-${departmentCode}-${randomNum}`;
  };

  const handleGenerateQR = () => {
    if (!name || !company || !department || !mainCategory || !assetName || !assetUpdateDate || !type) {
      alert("Please fill in all fields before generating the QR code.");
      return;
    }

    if (mainCategory === "Electronic items" && !serialNumber) {
      alert("Please enter a Serial Number for Electronics.");
      return;
    }

    const uniqueTrackingId = generateTrackingId();
    setTrackingId(uniqueTrackingId);
  };

  useEffect(() => {
    if (trackingId) {
      const qrData = JSON.stringify({
        name,
        company,
        department,
        mainCategory,
        assetName,
        type,
        assetUpdateDate,
        serialNumber: mainCategory === "Electronic items" ? serialNumber : null,
        trackingId,
      });

      setQrCodeData(qrData);
    }
  }, [trackingId]);

  const downloadQRCode = async () => {
    if (!qrCodeContainerRef.current) return;
    const canvas = await html2canvas(qrCodeContainerRef.current);
    const link = document.createElement("a");
    link.href = canvas.toDataURL("image/png");
    link.download = `QR_${trackingId}.png`;
    link.click();
  };

  return (
    <div className="asset-register">
      <div className="form-container">
        <h2>Asset Registration</h2>
        <input type="text" value={name} placeholder="Name" readOnly />
        <input type="text" value={assetName} onChange={(e) => setAssetName(e.target.value)} placeholder="Asset Name" />
        <input type="date" value={assetUpdateDate} onChange={(e) => setAssetUpdateDate(e.target.value)} />

        {mainCategory === "Electronic items" && (
          <input type="text" value={serialNumber} onChange={(e) => setSerialNumber(e.target.value)} placeholder="Serial Number" />
        )}

        <button onClick={handleGenerateQR}>Generate QR</button>
        {trackingId && (
          <div className="qr-container" ref={qrCodeContainerRef}>
            <p>Tracking ID: {trackingId}</p>
            {qrCodeData && <QRCode value={qrCodeData} size={150} />}
          </div>
        )}

        {qrCodeData && <button onClick={downloadQRCode}>Download QR Code</button>}
      </div>
    </div>
  );
};

export default AssetRegister;
