import React, { useState, useRef } from "react";
import { QRCode } from "react-qrcode-logo";
import html2canvas from "html2canvas"; // Library to capture HTML as an image
import "./assetRegister.css";

const AssetRegister = () => {
  const user = JSON.parse(localStorage.getItem("currentUser"));

  const [name, setName] = useState(user?.username || "");
  const [company, setCompany] = useState("");
  const [department, setDepartment] = useState("");
  const [category, setCategory] = useState("");
  const [assetName, setAssetName] = useState("");
  const [assetUpdateDate, setAssetUpdateDate] = useState("");
  const [qrCodeData, setQrCodeData] = useState(null);
  const [trackingId, setTrackingId] = useState("");

  const qrCodeRef = useRef(); // Reference to the QR code container

  // Function to generate a unique tracking ID
  const generateTrackingId = () => {
    return `${company}-${department}-${Date.now()}`;
  };

  const handleSubmit = () => {
    if (!name || !company || !department || !category || !assetName || !assetUpdateDate) {
      alert("Please fill in all fields before submitting.");
      return;
    }

    const uniqueTrackingId = generateTrackingId();
    setTrackingId(uniqueTrackingId);

    const assetData = {
      name,
      company,
      department,
      category,
      assetName,
      assetUpdateDate,
      trackingId: uniqueTrackingId,
    };

    console.log("Asset Data: ", assetData);
    alert("Asset Registered Successfully!");

    // Clear the input fields
    setName(user?.username || "");
    setCompany("");
    setDepartment("");
    setCategory("");
    setAssetName("");
    setAssetUpdateDate("");
    setQrCodeData(null);
    setTrackingId("");
  };

  const handleGenerateQR = () => {
    if (!name || !company || !department || !category || !assetName || !assetUpdateDate) {
      alert("Please fill in all fields before generating the QR code.");
      return;
    }

    const uniqueTrackingId = trackingId || generateTrackingId();
    setTrackingId(uniqueTrackingId);

    const qrData = JSON.stringify({
      name,
      company,
      department,
      category,
      assetName,
      assetUpdateDate,
      trackingId: uniqueTrackingId,
    });

    setQrCodeData(qrData);
  };

  const handleDownloadQR = async () => {
    if (!qrCodeRef.current) return;

    const canvas = await html2canvas(qrCodeRef.current);
    const link = document.createElement("a");
    link.download = "QRCode.png";
    link.href = canvas.toDataURL("image/png");
    link.click();
  };

  const handleDownloadTrackingData = () => {
    if (!trackingId) return;

    const trackingData = {
      name,
      company,
      department,
      category,
      assetName,
      assetUpdateDate,
      trackingId,
    };

    const blob = new Blob([JSON.stringify(trackingData, null, 2)], {
      type: "application/json",
    });
    const link = document.createElement("a");
    link.download = "TrackingData.json";
    link.href = URL.createObjectURL(blob);
    link.click();
  };

  return (
    <div>
      <div className="asset-register">
        <div className="form-container">
          <h2>Asset Registration</h2>
          <div className="input-box">
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Enter your Name"
              readOnly={!!user}
            />
            <input
              type="text"
              value={company}
              onChange={(e) => setCompany(e.target.value)}
              placeholder="Enter Company"
            />
            <input
              type="text"
              value={department}
              onChange={(e) => setDepartment(e.target.value)}
              placeholder="Enter Department"
            />
            <input
              type="text"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              placeholder="Enter Asset Category"
            />
            <input
              type="text"
              value={assetName}
              onChange={(e) => setAssetName(e.target.value)}
              placeholder="Enter Asset Name"
            />
            <input
              type="date"
              value={assetUpdateDate}
              onChange={(e) => setAssetUpdateDate(e.target.value)}
              placeholder="Select Asset Update Date"
            />
            <div className="button-group">
              <button className="button" onClick={handleSubmit}>
                Submit
              </button>
              <button className="button generate-btn" onClick={handleGenerateQR}>
                Generate QR
              </button>
            </div>
          </div>
          {qrCodeData && (
            <div className="qr-container">
              <h3>Generated QR Code</h3>
              <div ref={qrCodeRef}>
                <QRCode
                  value={qrCodeData}
                  size={150}
                  qrStyle="squares"
                  logoImage="https://via.placeholder.com/30"
                  logoWidth={40}
                />
              </div>
              <p>Tracking ID: {trackingId}</p>
              <button className="button download-btn" onClick={handleDownloadQR}>
                Download QR Code
              </button>
              <button className="button download-btn" onClick={handleDownloadTrackingData}>
                Download Tracking Data
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AssetRegister;
