import React, { useState, useRef, useEffect } from "react";
import { QRCode } from "react-qrcode-logo";
import html2canvas from "html2canvas";
import axios from "axios";
import "./assetRegister.css";

const AssetRegister = () => {
  const user = JSON.parse(localStorage.getItem("currentUser"));
  
  const [name, setName] = useState(user?.username || "");
  const [company, setCompany] = useState("");
  const [department, setDepartment] = useState("");
  const [mainCategory, setMainCategory] = useState("");
  const [type, setType] = useState("");
  const [categories, setCategories] = useState([]);
  const [types, setTypes] = useState([]);
  const [computerComponents, setComputerComponents] = useState("");
  const [fullSetData, setFullSetData] = useState({
    cpu: { assetName: "", serialNumber: "" },
    monitor: { assetName: "", serialNumber: "" },
    mouse: { assetName: "", serialNumber: "" },
    keyboard: { assetName: "", serialNumber: "" },
  });
  const [qrCodes, setQrCodes] = useState({});
  const [trackingIds, setTrackingIds] = useState({});

  const generateTrackingId = (component) => {
    const companyCodes = { Vella: "VE", "98 Acers": "98", "Ravana Pool Club": "RPC" };
    const departmentCodes = { IT: "IT", HR: "HR", Kitchen: "KT" };
    const companyCode = companyCodes[company] || "XX";
    const departmentCode = departmentCodes[department] || "XX";
    const serialSuffix = fullSetData[component].serialNumber.slice(-4) || "0000";
    return `${companyCode}-${departmentCode}-${component.toUpperCase()}-${serialSuffix}`;
  };

  const handleGenerateQR = () => {
    if (!name || !company || !department || !mainCategory || type !== "Computer" || computerComponents !== "fullSet") {
      alert("Please fill in all required fields.");
      return;
    }

    let newQrCodes = {};
    let newTrackingIds = {};

    Object.keys(fullSetData).forEach((component) => {
      const trackingId = generateTrackingId(component);
      newTrackingIds[component] = trackingId;
      newQrCodes[component] = JSON.stringify({
        name,
        company,
        department,
        mainCategory,
        type,
        assetName: fullSetData[component].assetName,
        serialNumber: fullSetData[component].serialNumber,
        trackingId,
      });
    });

    setTrackingIds(newTrackingIds);
    setQrCodes(newQrCodes);
  };

  const handleInputChange = (component, field, value) => {
    setFullSetData((prevData) => ({
      ...prevData,
      [component]: {
        ...prevData[component],
        [field]: value,
      },
    }));
  };

  return (
    <div className="asset-register">
      <h2>Asset Registration</h2>
      <input type="text" value={name} placeholder="Enter Name" readOnly />
      <select value={company} onChange={(e) => setCompany(e.target.value)}>
        <option value="">Select Company</option>
        <option value="Vella">Vella</option>
        <option value="98 Acers">98 Acers</option>
      </select>
      <select value={department} onChange={(e) => setDepartment(e.target.value)}>
        <option value="">Select Department</option>
        <option value="IT">IT</option>
        <option value="HR">HR</option>
      </select>
      <select value={mainCategory} onChange={(e) => setMainCategory(e.target.value)}>
        <option value="">Select Category</option>
        <option value="Electronic items">Electronic items</option>
      </select>
      {mainCategory && (
        <select value={type} onChange={(e) => setType(e.target.value)}>
          <option value="">Select Type</option>
          <option value="Computer">Computer</option>
        </select>
      )}

      {type === "Computer" && (
        <div>
          <label>
            <input
              type="radio"
              name="computerComponent"
              value="fullSet"
              onChange={() => setComputerComponents("fullSet")}
              checked={computerComponents === "fullSet"}
            /> Full Set
          </label>
        </div>
      )}

      {computerComponents === "fullSet" && (
        <div>
          {Object.keys(fullSetData).map((component) => (
            <div key={component}>
              <h4>{component.toUpperCase()}</h4>
              <input
                type="text"
                placeholder={`Enter ${component} Asset Name`}
                value={fullSetData[component].assetName}
                onChange={(e) => handleInputChange(component, "assetName", e.target.value)}
              />
              <input
                type="text"
                placeholder={`Enter ${component} Serial Number`}
                value={fullSetData[component].serialNumber}
                onChange={(e) => handleInputChange(component, "serialNumber", e.target.value)}
              />
            </div>
          ))}
          <button onClick={handleGenerateQR}>Generate QR Codes</button>
        </div>
      )}

      {Object.keys(qrCodes).length > 0 && (
        <div>
          <h3>Generated QR Codes</h3>
          {Object.keys(qrCodes).map((component) => (
            <div key={component}>
              <h4>{component.toUpperCase()} - ID: {trackingIds[component]}</h4>
              <QRCode value={qrCodes[component]} size={150} />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AssetRegister;
