import './adduserrole.css';
import React, { useState } from 'react';
import axios from 'axios';

const AddUsersRole = () => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [designation, setDesignation] = useState("");
    const [contact, setContact] = useState("");
    const [subjects, setSubjects] = useState({
        Admin: true,
        CompanyAdmin: false,
    });
    const [username, setUsername] = useState("");
    const [selectedOption, setSelectedOption] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [showPassword, setShowPassword] = useState(false);
    const [companyName, setCompanyName] = useState("");
    const [errors, setErrors] = useState({});
    const [passwordStrength, setPasswordStrength] = useState("");

    const validateForm = () => {
        let formErrors = {};
        if (!firstName) formErrors.firstName = "First Name is required.";
        if (!lastName) formErrors.lastName = "Last Name is required.";
        if (!designation) formErrors.designation = "Designation is required.";
        if (!contact) formErrors.contact = "Contact is required.";
        if (!username) formErrors.username = "Username is required.";
        if (!password) formErrors.password = "Password is required.";
        if (!confirmPassword) formErrors.confirmPassword = "Confirm Password is required.";
        if (!companyName) formErrors.companyName = "Company Name is required.";
        if (password && !validatePasswordStrength(password)) {
            formErrors.passwordStrength = "Password must be at least 8 characters, include uppercase, lowercase, and a number.";
        }
        if (password && confirmPassword && password !== confirmPassword) {
            formErrors.passwordMatch = "Passwords do not match.";
        }
        setErrors(formErrors);
        return Object.keys(formErrors).length === 0;
    };

    const validatePasswordStrength = (password) => {
        const strongPasswordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[A-Za-z\d@$!%*?&]{8,}$/;
        return strongPasswordRegex.test(password);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!validateForm()) return;

        const userData = {
            firstName,
            lastName,
            designation,
            contact,
            /*subjects,*/
            username,
            selectedOption,
            password,
            companyName
        };

        try {
            const result = await axios.post('http://localhost:9000/api/AdminUsersRole/AdminUsers', userData);
            ;
            console.log("User created successfully", result.data);
            handleReset();
        } catch (error) {
            console.error("Error creating user:", error);
        }
    };

    const handleReset = () => {
        setFirstName("");
        setLastName("");
        setDesignation("");
        setContact("");
        /*setSubjects({ Admin: true, CompanyAdmin: false });*/
        setUsername("");
        setSelectedOption("");
        setPassword("");
        setConfirmPassword("");
        setShowPassword(false);
        setCompanyName("");
        setErrors({});
        setPasswordStrength("");
    };

    return (
        <div className="Users">
            <h1>User Registration</h1>
            <fieldset>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="firstname">First Name*</label>
                    <input
                        type="text"
                        id="firstname"
                        value={firstName}
                        onChange={(e) => setFirstName(e.target.value)}
                        placeholder="Enter First Name"
                    />
                    {errors.firstName && <span className="error">{errors.firstName}</span>}

                    <label htmlFor="lastname">Last Name*</label>
                    <input
                        type="text"
                        id="lastname"
                        value={lastName}
                        onChange={(e) => setLastName(e.target.value)}
                        placeholder="Enter Last Name"
                    />
                    {errors.lastName && <span className="error">{errors.lastName}</span>}

                    <label htmlFor="designation">Designation*</label>
                    <input
                        type="text"
                        id="designation"
                        value={designation}
                        onChange={(e) => setDesignation(e.target.value)}
                        placeholder="Enter Designation"
                    />
                    {errors.designation && <span className="error">{errors.designation}</span>}

                    <label htmlFor="contact">Contact*</label>
                    <input
                        type="tel"
                        id="contact"
                        value={contact}
                        onChange={(e) => setContact(e.target.value)}
                        placeholder="Enter Contact Number"
                    />
                    {errors.contact && <span className="error">{errors.contact}</span>}

                    <label htmlFor="username">Username*</label>
                    <input
                        type="text"
                        id="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder="Enter Username"
                    />
                    {errors.username && <span className="error">{errors.username}</span>}

                    <label htmlFor="password">Password*</label>
                    <input
                        type={showPassword ? "text" : "password"}
                        id="password"
                        value={password}
                        onChange={(e) => {
                            setPassword(e.target.value);
                            setPasswordStrength(validatePasswordStrength(e.target.value) ? "Strong" : "Weak");
                        }}
                        placeholder="Enter Password"
                    />
                    {errors.password && <span className="error">{errors.password}</span>}
                    {errors.passwordStrength && <span className="error">{errors.passwordStrength}</span>}
                    <span className={passwordStrength === "Strong" ? "strong" : "weak"}>{passwordStrength}</span>

                    <label htmlFor="confirmPassword">Confirm Password*</label>
                    <input
                        type={showPassword ? "text" : "password"}
                        id="confirmPassword"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        placeholder="Confirm Password"
                    />
                    {errors.confirmPassword && <span className="error">{errors.confirmPassword}</span>}

                    <label>
                        <input
                            type="checkbox"
                            checked={showPassword}
                            onChange={() => setShowPassword(!showPassword)}
                        />
                        Show Password
                    </label>

                    <label>User Role</label>
                    <select
                        value={selectedOption}
                        onChange={(e) => setSelectedOption(e.target.value)}
                    >
                        <option value="">Select Role</option>
                        <option value="Admin">Admin</option>
                        <option value="CompanyAdmin">Company Admin</option>
                    </select>

                    <label htmlFor="companyName">Company Name*</label>
                    <input
                        type="text"
                        id="companyName"
                        value={companyName}
                        onChange={(e) => setCompanyName(e.target.value)}
                        placeholder="Enter Company Name"
                    />
                    {errors.companyName && <span className="error">{errors.companyName}</span>}

                    <button type="button" onClick={handleReset}>Reset</button>
                    <button type="submit">Submit</button>
                </form>
            </fieldset>
        </div>
    );
};

export default AddUsersRole;
